package mypack;


class Test1{
	static void display() {
		System.out.println("Display 1");
	}
	static void show() {
		System.out.println("Display static show");
	}
}







public class Staticmethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Test1.display();
		Test1.show();

	}

}
